      const newIsTieBreak = !game.is_tie_break;

      setGames((prevGames) =>
        prevGames.map((g) =>
          g.id === gameId
            ? {
                ...g,
                is_tie_break: newIsTieBreak,
                tie_break_pair1_points: 0,
                tie_break_pair2_points: 0,
              }
