                            // Validación: Verificar si algún jugador ya está en una pareja
                            const playersInPairs = players.filter((player) => {
                              const isInPair = pairs.some(
                                (pair) =>
                                  pair.player1_id === player.id ||
                                  pair.player2_id === player.id
                              );

                              if (isInPair) {
