                                    "🚨 PAREJA DUPLICADA DETECTADA:",
                                    player1.name,
                                    "+",
                                    player2.name
                                  );
                                  console.log("Existing pair:", existingPair);
                                }

                                return sameIds || sameNames;
                              });

