import React from "react";
import { Game, Match, Pair } from "../lib/database";
import { MatchResultCalculator } from "./MatchResultCalculator";
import { FlexibleMatchFinisher } from "./FlexibleMatchFinisher";

interface MatchStatusDisplayProps {
  match: Match;
  games: Game[];
  pairs: Pair[];
}

const MatchStatusDisplay: React.FC<MatchStatusDisplayProps> = ({
  match,
  games,
  pairs,
}) => {
  const calculateCurrentStatus = () => {
    // Usar la nueva lógica flexible
    return FlexibleMatchFinisher.calculateMatchStatus(match, games, pairs);
  };

  const status = calculateCurrentStatus();
  const pair1 = pairs.find((p) => p.id === match.pair1_id);
  const pair2 = pairs.find((p) => p.id === match.pair2_id);

  const pair1Name = pair1
    ? `${pair1.player1?.name} y ${pair1.player2?.name}`
    : "Pareja 1";
  const pair2Name = pair2
    ? `${pair2.player1?.name} y ${pair2.player2?.name}`
    : "Pareja 2";

  return (
    <div className="match-status-display">
      <h4>📊 Estado del Partido</h4>

      <div className="status-grid">
        <div className="status-item">
          <strong>Juegos registrados:</strong> {games.length}
        </div>

        <div className="status-item">
          <strong>Pareja 1 ({pair1Name}):</strong> {status.pair1Games} juegos
        </div>

        <div className="status-item">
          <strong>Pareja 2 ({pair2Name}):</strong> {status.pair2Games} juegos
        </div>

        <div className="status-item">
          <strong>Resultado:</strong>{" "}
          {status.isTie
            ? "🤝 Empate"
            : `🏆 Ganador: ${
                status.winnerId === match.pair1_id ? pair1Name : pair2Name
              }`}
        </div>

        <div className="status-item">
          <strong>Puede finalizar:</strong>{" "}
          <span className={status.canFinish ? "status-ok" : "status-error"}>
            {status.canFinish ? "✅ Sí" : "❌ No"}
          </span>
        </div>

        <div className="status-item">
          <strong>Razón:</strong> {status.reason}
        </div>
      </div>

      <div className="games-detail">
        <h5>Detalle de Juegos:</h5>

        <div className="tie-explanation">
          <h6>📋 Información sobre Empates:</h6>
          <ul>
            <li>
              <strong>Empates en Juegos:</strong> Si un juego termina empatado
              (ej: 3-3), ninguna pareja gana juego
            </li>
            <li>
              <strong>Empates en Tie-Breaks:</strong> Si un tie-break termina
              empatado, ninguna pareja gana juego
            </li>
            <li>
              <strong>Empates en Partidos:</strong> Si el total está empatado,
              ambas parejas ganan 1 set
            </li>
            <li>
              <strong>Sin Tiempo:</strong> Se pueden registrar empates cuando no
              hay tiempo para terminar
            </li>
            <li>
              <strong>Finalización Libre:</strong> Se puede finalizar el partido
              sin importar si llegaron a 6 puntos
            </li>
          </ul>
        </div>

        {games.map((game, index) => (
          <div key={game.id} className="game-detail">
            <strong>Juego {index + 1}:</strong>{" "}
            {game.is_tie_break ? (
              <>
                Tie-break: {game.tie_break_pair1_points || 0} -{" "}
                {game.tie_break_pair2_points || 0}
                {game.tie_break_pair1_points === game.tie_break_pair2_points &&
                game.tie_break_pair1_points > 0
                  ? " (Empatado - 1 juego cada uno)"
                  : game.tie_break_pair1_points > game.tie_break_pair2_points
                  ? " (Gana Pareja 1)"
                  : game.tie_break_pair2_points > game.tie_break_pair1_points
                  ? " (Gana Pareja 2)"
                  : " (En progreso)"}
              </>
            ) : (
              <>
                {game.pair1_games || 0} - {game.pair2_games || 0}
                {game.pair1_games === game.pair2_games && game.pair1_games > 0
                  ? " (Empatado - 1 juego cada uno)"
                  : game.pair1_games > game.pair2_games
                  ? " (Gana Pareja 1)"
                  : game.pair2_games > game.pair1_games
                  ? " (Gana Pareja 2)"
                  : " (En progreso)"}
              </>
            )}
          </div>
        ))}
      </div>
    </div>
  );
};

export default MatchStatusDisplay;
