import React from "react";

interface TournamentIdProps {
  id: string;
}

export const TournamentId: React.FC<TournamentIdProps> = ({ id }) => {
  const shortId = id.slice(-4);

  return (
    <div className="tournament-id-badge">
      <span className="tournament-id-text">#{shortId}</span>
    </div>
  );
};
