import { createClient } from "@supabase/supabase-js";

const supabaseUrl = process.env.REACT_APP_SUPABASE_URL;
const supabaseKey = process.env.REACT_APP_SUPABASE_ANON_KEY;

console.log("üîß Configuraci√≥n de Supabase:");
console.log("URL:", supabaseUrl ? "‚úÖ Configurada" : "‚ùå No configurada");
console.log("Key:", supabaseKey ? "‚úÖ Configurada" : "‚ùå No configurada");

if (!supabaseUrl || !supabaseKey) {
  console.error("‚ùå Error: Supabase environment variables are not configured!");
  console.error("Please create a .env file with the following variables:");
  console.error("REACT_APP_SUPABASE_URL=your_supabase_project_url");
  console.error("REACT_APP_SUPABASE_ANON_KEY=your_supabase_anon_key");
  console.error(
    "You can get these values from your Supabase project settings."
  );
}

export const supabase = createClient(
  supabaseUrl || "https://placeholder.supabase.co",
  supabaseKey || "placeholder_key"
);

// Funci√≥n para probar la conexi√≥n
export const testConnection = async () => {
  try {
    console.log("üß™ Probando conexi√≥n a Supabase...");
    const { error } = await supabase
      .from("tournaments")
      .select("count")
      .limit(1);

    if (error) {
      console.error("‚ùå Error de conexi√≥n:", error);
      return false;
    }

    console.log("‚úÖ Conexi√≥n exitosa a Supabase");
    return true;
  } catch (err) {
    console.error("‚ùå Error de conexi√≥n:", err);
    return false;
  }
};
